mapper.select:
select b.id, b.equipment_id, b.equipment_name, b.model, b.borrower, b.borrow_qty, b.borrow_time, b.location, b.project_id, b.project_name, b.tutor_name, b.audit_status, b.audit_reason, b.create_by, b.create_time, b.update_by, b.update_time, b.remark,
/* ✨✨✨ 修改点：只统计 return_qty (归还数量) ✨✨✨ */ ( SELECT IFNULL(SUM(r.return_qty), 0) FROM camera_return r WHERE r.borrow_id = b.id AND r.audit_status = '1' ) AS return_count /* ✨✨✨ 修改结束 ✨✨✨ */
from fieldwork_borrowing b
and b.equipment_name like concat('%', #{equipmentName}, '%') and b.borrower = #{borrower} and b.project_name like concat('%', #{projectName}, '%')
AND b.audit_status = '1'
AND b.create_by = #{createBy}
AND b.audit_status != '1'
AND b.audit_status = #{auditStatus}
order by b.create_time desc

select
    b.id, b.equipment_id, b.equipment_name, b.model, b.borrower, b.borrow_qty, b.borrow_time, b.location, b.project_id, b.project_name, b.tutor_name, b.audit_status, b.audit_reason, b.create_by, b.create_time, b.update_by, b.update_time, b.remark,
    /* ✨✨✨ 修改点：详情查询也只统计 return_qty ✨✨✨ */
    (
        SELECT IFNULL(SUM(r.return_qty), 0)
        FROM camera_return r
        WHERE r.borrow_id = b.id AND r.audit_status = '1'
    ) AS return_count
from fieldwork_borrowing b
where b.id = #{id}

mapper.update:
update fieldwork_borrowing
equipment_id = #{equipmentId}, equipment_name = #{equipmentName}, model = #{model}, borrower = #{borrower}, borrow_qty = #{borrowQty}, borrow_time = #{borrowTime}, location = #{location}, project_id = #{projectId}, project_name = #{projectName}, tutor_name = #{tutorName}, audit_status = #{auditStatus}, audit_reason = #{auditReason}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where id = #{id}
