mapper.select:
select u.user_id, u.dept_id, u.nick_name, u.user_name, u.email, u.avatar, u.phonenumber, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark,
u.ethnicity, u.student_id, u.undergrad_univ, u.master_univ, u.enrollment_year, u.degree_type, u.major, u.address, u.employer, u.current_status, u.emergency_contact, u.emergency_relation, u.emergency_phone, u.audit_status,
d.dept_name, d.leader from sys_user u
left join sys_dept d on u.dept_id = d.dept_id
where u.del_flag = '0'

    AND u.user_id = #{userId}


    AND u.user_name like concat('%', #{userName}, '%')


    AND u.nick_name like concat('%', #{nickName}, '%')


    AND u.status = #{status}


    AND u.phonenumber like concat('%', #{phonenumber}, '%')

AND date_format(u.create_time,'%Y%m%d') >= date_format(#{params.beginTime},'%Y%m%d')

AND date_format(u.create_time,'%Y%m%d') <= date_format(#{params.endTime},'%Y%m%d')


    AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))



    AND u.student_id = #{studentId}


    AND u.audit_status = #{auditStatus}


    AND u.nick_name like concat('%', #{nickName}, '%')



    AND u.enrollment_year = #{enrollmentYear}



    AND u.degree_type = #{degreeType}



    AND u.major = #{major}



    AND u.current_status = #{currentStatus}


${params.dataScope}

select distinct u.user_id, u.dept_id, u.user_name, u.nick_name, u.email, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

mapper.update:
update sys_user
dept_id = #{deptId}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, ethnicity = #{ethnicity}, student_id = #{studentId}, undergrad_univ = #{undergradUniv}, master_univ = #{masterUniv}, enrollment_year = #{enrollmentYear}, degree_type = #{degreeType}, major = #{major}, address = #{address}, employer = #{employer}, current_status = #{currentStatus}, emergency_contact = #{emergencyContact}, emergency_relation = #{emergencyRelation}, emergency_phone = #{emergencyPhone}, audit_status = #{auditStatus}, update_time = sysdate()
where user_id = #{userId}
